import static com.example.softmills.phlog.ui.album.view.AllAlbumImgActivity.ALBUM_ID;
import static com.example.softmills.phlog.ui.album.view.AllAlbumImgActivity.ALL_ALBUM_IMAGES;
 import static com.example.softmills.phlog.ui.album.view.AllAlbumImgActivity.SELECTED_IMG_ID;

/**
 * Created by abdalla_maged on 11/4/2018.
 */
public class AlbumPreviewActivity extends BaseActivity implements AlbumPreviewActivityView {


    public static final String ALBUM_PREVIEW_ID="album_preview_id";
    private List<AlbumGroup> albumGroupList;
    private  List<AlbumImg> albumList;
    private AlbumAdapter albumAdapter;
    private ProgressBar albumPreviewProgress;
    private CustomRecyclerView albumRv;
    private PagingController pagingController;
    private AlbumPreviewActivityPresenter albumPreviewActivityPresenter;

    @Override
    protected void onCreate(@Nullable Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_album_preview);
        initPresenter();
        initView();
        initListener();
    }


    @Override
    public void addFragment(int containerID, Fragment fragment, String tag, boolean addBackStack) {
        super.addFragment(containerID, fragment, tag, addBackStack);
    }

    @Override
    public void initView() {

        albumRv = findViewById(R.id.album_rv);
        albumPreviewProgress = findViewById(R.id.user_profile_progress_bar);

        // Set adapter object.
        prepareAlbum();
        albumAdapter = new AlbumAdapter(getBaseContext(), albumGroupList);
        albumRv.setAdapter(albumAdapter);
        albumAdapter.notifyDataSetChanged();


    }

    @Override
    public void initPresenter() {
        albumPreviewActivityPresenter = new AlbumPreviewActivityPresenterImpl(getBaseContext(), this);
    }

    private void initListener() {


        pagingController = new PagingController(albumRv) {
            @Override
            public void getPagingControllerCallBack(int page) {
                albumPreviewActivityPresenter.getSelectedSearchAlbum("albumID", String.valueOf(page));
            }
        };

        albumAdapter.onAlbumImageClicked = albumImg -> {
            Intent intent =new Intent(this,AllAlbumImgActivity.class);
             intent.putExtra(ALBUM_ID,"albumID_here");
             intent.putExtra(SELECTED_IMG_ID,albumImg.albumImgId);
             intent.putParcelableArrayListExtra(ALL_ALBUM_IMAGES, (ArrayList<? extends Parcelable>) albumList);
             startActivity(intent);

        };
    }

    @Override
    public void showToast(String msg) {
        super.showToast(msg);
    }


    public void prepareAlbum() {
       albumList = new ArrayList<AlbumImg>();
        String pathOne = "data:image/jpeg;base64,";
        String pathTwo = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTmJAcSbVi6nOeynZm19HU-XFsBKrJKJkeBQDBLZi1LOO6eMg8i";
        String pathThree = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSNgSa_VWB6nZBZtFGseN7BLnFx1MLEyS_P4_Ez1-HYp2Tb1VLr";
        String pathFour = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSi0Nl7UKE5moLq_1KwWFo5GRtwTqPtuI8B--8b1FL_TaadSNqB";
        AlbumImg album1 = new AlbumImg();
        album1.AlbumImg = pathOne;
        AlbumImg album2 = new AlbumImg();
        album2.AlbumImg = pathTwo;
        AlbumImg album3 = new AlbumImg();
        album3.AlbumImg = pathThree;
        AlbumImg album4 = new AlbumImg();
        album4.AlbumImg = pathOne;
        AlbumImg album5 = new AlbumImg();
        album5.AlbumImg = pathTwo;
        AlbumImg album6 = new AlbumImg();
        album6.AlbumImg = pathThree;
        AlbumImg album7 = new AlbumImg();
        album7.AlbumImg = pathFour;
        AlbumImg album8 = new AlbumImg();
        album8.AlbumImg = pathOne;


        albumList.add(album1);
        albumList.add(album2);
        albumList.add(album3);
        albumList.add(album4);
        albumList.add(album5);
        albumList.add(album6);
        albumList.add(album7);
        albumList.add(album8);

        albumList.add(album1);
        albumList.add(album2);
        albumList.add(album3);
        albumList.add(album4);
        albumList.add(album5);
        albumList.add(album6);
        albumList.add(album7);
//        albumList.add(album8);

        AlbumGroup singleGroup = new AlbumGroup();
        albumGroupList = new ArrayList<AlbumGroup>();


        int albumGroupCounter = 0;
        for (int i = 0; i < albumList.size(); i++) {
            albumGroupCounter++;
            singleGroup.albumGroupList.add(albumList.get(i));

            if (albumGroupCounter == 4 || i == albumList.size() - 1) {
                albumGroupList.add(singleGroup);
                albumGroupCounter = 0;
                singleGroup = new AlbumGroup();
            }


        }


    }

    @Override
    public void viewAlumPreview(AlbumPreviewResponseData albumPreviewResponseData) {


    }

    @Override
    public void viewAlbumPreviewProgress(boolean state) {
        if (state) {
            albumPreviewProgress.setVisibility(View.VISIBLE);

        } else {
            albumPreviewProgress.setVisibility(View.GONE);

        }

    }
}
